//Main holder for Userdashboard 

import React, { useState, useEffect } from 'react';
import clsx from 'clsx';
import { makeStyles } from '@material-ui/core/styles';
import CssBaseline from '@material-ui/core/CssBaseline';
import Drawer from '@material-ui/core/Drawer';
import Box from '@material-ui/core/Box';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import List from '@material-ui/core/List';
import Typography from '@material-ui/core/Typography';
import Divider from '@material-ui/core/Divider';
import IconButton from '@material-ui/core/IconButton';
import Badge from '@material-ui/core/Badge';
import Container from '@material-ui/core/Container';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import Link from '@material-ui/core/Link';
import MenuIcon from '@material-ui/icons/Menu';
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft';
import NotificationsIcon from '@material-ui/icons/Notifications';

import { mainListItems, secondaryListItems } from '../Components/SideNavbar';
import StockComponent from '../Components/StockComponent/index';
import StockWatch from '../Components/StockWatch/index';
import Portfolio from '../Components/Portfolio/index';

import API from "../utils/API.js";


function Stocks() {

  function Copyright() {
  return (
  <Typography variant="body2" color="textSecondary" align="center">
    {'Copyright © '}
    <Link color="inherit" href="https://material-ui.com/">
      Your Website
    </Link>{' '}
    {new Date().getFullYear()}
    {'.'}
  </Typography>
  );
  }
  const drawerWidth = 240;
  const useStyles = makeStyles((theme) => ({
  root: {
  display: 'flex',
  },
  toolbar: {
  paddingRight: 24, // keep right padding when drawer closed
  },
  toolbarIcon: {
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'flex-end',
  padding: '0 8px',
  ...theme.mixins.toolbar,
  },
  appBar: {
  zIndex: theme.zIndex.drawer + 1,
  transition: theme.transitions.create(['width', 'margin'], {
    easing: theme.transitions.easing.sharp,
    duration: theme.transitions.duration.leavingScreen,
  }),
  },
  appBarShift: {
  marginLeft: drawerWidth,
  width: `calc(100% - ${drawerWidth}px)`,
  transition: theme.transitions.create(['width', 'margin'], {
    easing: theme.transitions.easing.sharp,
    duration: theme.transitions.duration.enteringScreen,
  }),
  },
  menuButton: {
  marginRight: 36,
  },
  menuButtonHidden: {
  display: 'none',
  },
  title: {
  flexGrow: 1,
  },
  drawerPaper: {
  position: 'relative',
  whiteSpace: 'nowrap',
  width: drawerWidth,
  transition: theme.transitions.create('width', {
    easing: theme.transitions.easing.sharp,
    duration: theme.transitions.duration.enteringScreen,
  }),
  },
  drawerPaperClose: {
  overflowX: 'hidden',
  transition: theme.transitions.create('width', {
    easing: theme.transitions.easing.sharp,
    duration: theme.transitions.duration.leavingScreen,
  }),
  width: theme.spacing(7),
  [theme.breakpoints.up('sm')]: {
    width: theme.spacing(9),
  },
  },
  appBarSpacer: theme.mixins.toolbar,
  content: {
  flexGrow: 1,
  height: '100vh',
  overflow: 'auto',
  },
  container: {
  paddingTop: theme.spacing(4),
  paddingBottom: theme.spacing(4),
  },
  paper: {
  padding: theme.spacing(2),
  display: 'flex',
  overflow: 'auto',
  flexDirection: 'column',
  },
  fixedHeight: {
  height: 250,
  },

  small: {
  width: theme.spacing(3),
  height: theme.spacing(3),
  },
  large: {
  width: theme.spacing(7),
  height: theme.spacing(7),
  },
  }));


  // function NewScript(src){
  //         let script = document.createElement('script');
  //               script.src = src;
  //               script.async = true;

  //         document.head.appendChild(script)
  // }






  const [company, setCompany] = useState({})
  const [stock, setStock] = useState({})

    useEffect(() => {
      NewScript('/static/chartjs-chart-financial.js');
      async function load(){
        const stockss = await loadStocks();
        await searchStocks(stockss)
      }
      load();
    }, [])

  function loadStocks(){
    return API.getAllStocks()
      .then(res => {
        setStock(res.data)
        return(res.data)
      })
      .catch(err => console.log(err));
    };

   async function searchStocks(stockss){ 
    await API.getStockPrices(stockss)
      .then(res =>{
        setCompany(res.data)})
      .catch(err => console.log(err));
  }




  const classes = useStyles();
  const [open, setOpen] = React.useState(true);
  const handleDrawerOpen = () => {
    setOpen(true);
  };
  const handleDrawerClose = () => {
    setOpen(false);
  };
  const fixedHeightPaper = clsx(classes.paper, classes.fixedHeight);

  return (

  <div className={classes.root}>
  <CssBaseline />
  <AppBar position="absolute" className={clsx(classes.appBar, open && classes.appBarShift)}>
  <Toolbar className={classes.toolbar}>
    <IconButton
      edge="start"
      color="primary"
      aria-label="open drawer"
      onClick={handleDrawerOpen}
      className={clsx(classes.menuButton, open && classes.menuButtonHidden)}
    >
      <MenuIcon />
    </IconButton>
    <Typography component="h1" variant="h6" color="inherit" noWrap className={classes.title}>
      Coins N' Stockin'
    </Typography>
    <IconButton color="inherit">
      <Badge badgeContent={4} color="secondary">
        <NotificationsIcon />
      </Badge>
    </IconButton>

    {/*User picture ! */}


  </Toolbar>
  </AppBar>


  <Drawer
  variant="permanent"
  classes={{
    paper: clsx(classes.drawerPaper, !open && classes.drawerPaperClose),
  }}
  open={open}

  >
  <div className={classes.toolbarIcon}>
    <IconButton  onClick={handleDrawerClose}>
      <ChevronLeftIcon />
    </IconButton>
  </div>
  <Divider />
  <List>{mainListItems}</List>
  <Divider />
  <List>{secondaryListItems}</List>
  </Drawer>


  <main className={classes.content}>
  <div className={classes.appBarSpacer} />
  <Container maxWidth="lg" className={classes.container}>
  <script src="../node_modules/chart.js/dist/Chart.bundle.js"></script>

    <Grid container spacing = {6}>
      {/* Chart.js with stocks */}

      <Grid item xs={12} md={6} lg={6}>
        <Paper className={classes.paper}>
          <Portfolio />
        </Paper>
      </Grid>
      <Grid item xs={12} md={6} lg={12}>
        <Paper className={fixedHeightPaper}>
          <StockComponent />
        </Paper>
      </Grid>

      <Grid item xs={12} md={6} lg={12}>
        <Paper className={fixedHeightPaper}>

          <StockWatch stockPrice={company} />
        </Paper>
      </Grid>

    </Grid>
    <Box pt={4}>
      <Copyright />
    </Box>
  </Container>
  </main>
  </div>
        )
}

export default Stocks;
